import * as React from 'react';
import { fireEvent, screen, waitFor } from '@testing-library/react-native';
import { renderWithProviders } from '@/utils/test-utils';
import AppLinkingScreen from '@/components/Apps/AppLinkingScreen';
import clearAllMocks = jest.clearAllMocks;
import { PreloadedState } from '@reduxjs/toolkit';
import {
  initialAppsState,
  selectApplinkingStep,
  setApps,
  setLinkingAppInfo,
} from '@/reducer/appsSlice';
import { app_linking_steps } from '@/utils/constants';
import { setupStore } from '@/store';
import i18next from 'i18next';

const mockNavigation = {
  navigate: jest.fn(),
  dispatch: jest.fn(),
  goBack: jest.fn(),
};

jest.mock('@react-navigation/native', () => {
  const actualNav = jest.requireActual('@react-navigation/native');
  return {
    ...actualNav,
    useNavigation: () => ({
      navigate: mockNavigation.navigate,
      dispatch: mockNavigation.dispatch,
      goBack: mockNavigation.goBack,
    }),
  };
});

describe('AppLinkingScreen', () => {
  const preloadedState: PreloadedState<RootState> = {
    apps: {
      ...initialAppsState,
      appLinkingStep: app_linking_steps.WAITING_USER_CONFIRMATION,
    },
  };
  const app = {
    id: 'testApp',
    name: 'testApp',
    context: 'testContext',
    verifications: ['BrightID'],
    logo: 'data:image/png;base64,',
    url: 'https://brightid.org',
    assignedSponsorships: 33167,
    unusedSponsorships: 4451,
    testing: false,
    idsAsHex: false,
    usingBlindSig: false,
    verificationExpirationLength: 0,
    sponsorPublicKey: 'D2BfstFLgILdH8Q3taY0Jl8yVuWejVR9cJSgm8OwQwo=',
    nodeUrl: '',
    soulbound: false,
    callbackUrl: '',
    sponsoring: true,
    verificationUrl: '',
  };
  const linkingAppInfo: LinkingAppInfo = {
    appId: app.id,
    appUserId: '123',
    v: 5,
    baseUrl: '',
  };
  let store;

  beforeEach(() => {
    store = setupStore(preloadedState);
    store.dispatch(setApps([app]));
    store.dispatch(setLinkingAppInfo(linkingAppInfo));
  });

  afterEach(() => {
    clearAllMocks();
  });

  it('user can cancel app linking', () => {
    renderWithProviders(<AppLinkingScreen />, {
      store,
    });
    screen.debug();
    fireEvent.press(screen.getByTestId('RejectLinking'));
    const step = selectApplinkingStep(store.getState());
    expect(step).toBe(app_linking_steps.IDLE);
  });

  it('linking fails without API', async () => {
    renderWithProviders(<AppLinkingScreen />, {
      store,
    });
    // should show app name
    screen.getByText(app.name, {
      exact: false,
    });
    // confirm linking
    fireEvent.press(screen.getByTestId('ConfirmLinking'));

    // should end up in error case as no API is available
    await waitFor(() => {
      screen.getByText(i18next.t('apps.alert.title.linkingFailed'));
    });
    screen.getByText('No BrightID node API available. Please try again later.');
  });
});
